import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(debugShowCheckedModeBanner: false, home: HomeScreen());
  }
}

class HomeScreen extends StatelessWidget {
  void addToCart(BuildContext context, String item) {
    ScaffoldMessenger.of(context).showSnackBar(
      SnackBar(
        content: Text("You selected $item"),
        duration: const Duration(seconds: 3),
      ),
    );
  }

  final List<Map<String, dynamic>> products = const [
    {
      'icon': Icons.tour,
      'name': 'Beach Gateway',
      'description': 'Pick Best Tour For Yourself',
    },
    {
      'icon': Icons.car_rental,
      'name': 'Packing Essentials',
      'description': 'Choose what you like',
    },
    {
      'icon': Icons.attractions,
      'name': 'Activity',
      'description': 'Activity you like',
    },
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      appBar: AppBar(
        title: Text(
          "TravelBuddy",
          style: TextStyle(
            color: Colors.deepOrange,
            fontSize: 32,
            fontWeight: FontWeight.bold,
          ),
        ),
        centerTitle: true,
        backgroundColor: Colors.white,
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.start,
          crossAxisAlignment: CrossAxisAlignment.center,
          children: [
            SizedBox(height: 20),
            // Heading
            Text(
              "Explore The World Together ",
              style: TextStyle(
                color: Colors.deepOrangeAccent,
                fontSize: 24,
                fontWeight: FontWeight.bold,
              ),
            ),

            SizedBox(height: 10),
            // Cards
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                // Cards
                CustomCard(
                  imageUrl:
                      "data:image/jpeg;base64,",
                  description: "Destinations",
                ),
                SizedBox(width: 10, height: 10),
                CustomCard(
                  imageUrl:
                      "data:image/jpeg;base64,",
                  description: "Travel Tips",
                ),
                SizedBox(width: 10, height: 10),
                CustomCard(
                  imageUrl:
                      "data:image/jpeg;base64,",
                  description: "Activities",
                ),
              ],
            ),

            // List
            Expanded(
              child: ListView.builder(
                itemCount: products.length,
                itemBuilder: (context, index) {
                  final product = products[index];
                  return ListTile(
                    leading: Icon(product['icon'], color: Colors.deepOrange),
                    title: Text(product['name']),
                    subtitle: Text(product['description']),
                    onTap: () => addToCart(context, product['name']),
                  );
                },
              ),
            ),

            TextButton(
              onPressed: null,
              style: TextButton.styleFrom(
                backgroundColor: Colors.blue,
                foregroundColor: Colors.white,
                padding: EdgeInsets.symmetric(horizontal: 20, vertical: 12),
              ),
              child: Text("Start Your Journey"),
            ),
          ],
        ),
      ),
    );
  }
}

// Card Widget
class CustomCard extends StatelessWidget {
  final String imageUrl;
  final String description;

  CustomCard({required this.imageUrl, required this.description});

  @override
  Widget build(BuildContext context) {
    return Card(
      color: Colors.white,
      elevation: 0,
      child: Padding(
        padding: EdgeInsets.all(0),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.start,
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Image.network(imageUrl, width: 200, height: 200),
            Text(description, style: TextStyle(fontSize: 16)),
          ],
        ),
      ),
    );
  }
}
